import { useState, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import "./OrderConfirmation.css";

const OrderConfirmation = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("pending"); // pending, processing, paid, failed
  const [paymentMethod, setPaymentMethod] = useState("Cash On Delivery");
  const [transactionId, setTransactionId] = useState("");
  const [processingPayment, setProcessingPayment] = useState(false);
  const [upiId, setUpiId] = useState("");

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        // Call the GET order API to fetch specific order details - using the correct endpoint
        const response = await fetch(`http://localhost:5002/orders/${orderId}`, {
          headers: {
            "Authorization": `Bearer ${localStorage.getItem("token")}` // Include auth token
          }
        });

        if (!response.ok) {
          throw new Error("Failed to fetch order details");
        }

        const data = await response.json();
        setOrder(data);
        
        // Set payment method from order data
        if (data.paymentMode) {
          setPaymentMethod(data.paymentMode);
        }
        
        // Check if payment is already completed for this order
        if (data.paymentStatus === "paid") {
          setPaymentStatus("paid");
          setTransactionId(data.transactionId || "");
        } else if (data.paymentMode === "Cash On Delivery") {
          // For COD orders, payment status is not required
          setPaymentStatus("cod");
        }
      } catch (error) {
        console.error("Error fetching order:", error);
        setError("Could not load your order details. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    if (orderId) {
      fetchOrderDetails();
    }
  }, [orderId]);

  const processPayment = async () => {
    setProcessingPayment(true);
    setPaymentStatus("processing");
    
    try {
      // Validate UPI ID if payment method is UPI
      if (paymentMethod === "UPI" && !upiId) {
        throw new Error("Please enter a valid UPI ID");
      }
      
      const paymentData = {
        orderId: orderId,
        paymentMode: paymentMethod,
        amount: order?.products.reduce((sum, item) => sum + (item.price * item.quantity), 0).toFixed(2),
        status: "paid"
      };
      
      // Add transaction ID - in a real app this would come from payment gateway
      paymentData.transactionId = "TXN" + Math.floor(Math.random() * 1000000000);
      
      // If UPI, add UPI ID
      if (paymentMethod === "UPI") {
        paymentData.upiId = upiId;
      }
      
      // Call the order update API to update payment status - using the correct endpoint
      const response = await fetch(`http://localhost:5002/orders/${orderId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({
          paymentStatus: "paid",
          paymentMode: paymentMethod,
          transactionId: paymentData.transactionId
        })
      });

      if (!response.ok) {
        throw new Error("Payment processing failed");
      }

      // Update the payment status and transaction ID
      setPaymentStatus("paid");
      setTransactionId(paymentData.transactionId);
      
      // Get updated order data - using the correct endpoint
      const updatedOrderResponse = await fetch(`http://localhost:5002/orders/${orderId}`, {
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        }
      });
      
      if (updatedOrderResponse.ok) {
        const updatedOrderData = await updatedOrderResponse.json();
        setOrder(updatedOrderData);
      }
      
      // Navigate to order success page after successful payment
      navigate(`/order-success/${orderId}`);
    } catch (error) {
      console.error("Payment error:", error);
      setPaymentStatus("failed");
      setError(error.message || "Payment processing failed. Please try again.");
    } finally {
      setProcessingPayment(false);
    }
  };

  const continueWithCOD = async () => {
    try {
      setProcessingPayment(true);
      
      // Update the order with COD payment method - using the correct endpoint
      const response = await fetch(`http://localhost:5002/orders/${orderId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({
          paymentStatus: "pending", // For COD, payment is pending until delivery
          paymentMode: "Cash On Delivery"
        })
      });

      if (!response.ok) {
        throw new Error("Failed to update order");
      }
      
      // Navigate to order success page
      navigate(`/order-success/${orderId}`);
    } catch (error) {
      console.error("Error updating order:", error);
      setError("Failed to place order. Please try again.");
    } finally {
      setProcessingPayment(false);
    }
  };

  if (loading) {
    return (
      <div className="order-confirmation-page">
        <Header />
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading your order details...</p>
        </div>
        <Footer />
      </div>
    );
  }

  if (error) {
    return (
      <div className="order-confirmation-page">
        <Header />
        <div className="error-container">
          <h2>Something went wrong</h2>
          <p>{error}</p>
          <Link to="/" className="back-home-btn">Back to Home</Link>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="order-confirmation-page">
      <Header />
      
      <div className="confirmation-container">
        {paymentStatus === "paid" ? (
          <div className="success-icon">
            <svg viewBox="0 0 24 24" width="60" height="60">
              <path fill="currentColor" d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" />
            </svg>
          </div>
        ) : (
          <div className="payment-pending-icon">
            <svg viewBox="0 0 24 24" width="60" height="60">
              <path fill="currentColor" d="M11 17h2v-6h-2v6zm1-15C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8zm-1-13h2v2h-2V7z" />
            </svg>
          </div>
        )}
        
        <h1>
          {paymentStatus === "paid" ? "Payment Successful!" : 
           paymentStatus === "cod" ? "Order Placed Successfully!" : 
           "Complete Your Order"}
        </h1>
        <p className="order-id">Order ID: #{orderId}</p>
        
        {paymentStatus === "paid" ? (
          <p className="thank-you-message">
            Thank you for your order. We'll send you a confirmation email shortly.
          </p>
        ) : paymentStatus === "cod" ? (
          <p className="thank-you-message">
            Thank you for your order. Your order will be delivered soon and payment will be collected at delivery.
          </p>
        ) : (
          <p className="payment-message">
            Please select your payment method to complete your order.
          </p>
        )}
        
        <div className="order-details">
          <h2>Order Details</h2>
          
          <div className="order-info-section">
            <div className="order-info-row">
              <span className="info-label">Order Date:</span>
              <span className="info-value">{new Date(order?.createdAt || Date.now()).toLocaleDateString()}</span>
            </div>
            <div className="order-info-row">
              <span className="info-label">Payment Method:</span>
              <span className="info-value">
                {paymentStatus === "paid" || paymentStatus === "cod" ? paymentMethod : 
                  <select 
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    disabled={processingPayment}
                  >
                    <option value="Cash On Delivery">Cash On Delivery</option>
                    <option value="Credit Card">Credit Card</option>
                    <option value="Debit Card">Debit Card</option>
                    <option value="UPI">UPI</option>
                    <option value="Net Banking">Net Banking</option>
                  </select>
                }
              </span>
            </div>
            
            {/* Show UPI ID field if UPI is selected */}
            {paymentMethod === "UPI" && paymentStatus !== "paid" && paymentStatus !== "cod" && (
              <div className="order-info-row">
                <span className="info-label">UPI ID:</span>
                <span className="info-value">
                  <input 
                    type="text" 
                    value={upiId} 
                    onChange={(e) => setUpiId(e.target.value)}
                    placeholder="Enter your UPI ID"
                    disabled={processingPayment}
                  />
                </span>
              </div>
            )}
            
            {paymentStatus === "paid" && (
              <div className="order-info-row">
                <span className="info-label">Transaction ID:</span>
                <span className="info-value">{transactionId}</span>
              </div>
            )}
            <div className="order-info-row">
              <span className="info-label">Delivery Address:</span>
              <span className="info-value">{order?.deliveryAddress}</span>
            </div>
            <div className="order-info-row">
              <span className="info-label">Phone:</span>
              <span className="info-value">{order?.phone}</span>
            </div>
          </div>
          
          <h3>Items</h3>
          <div className="order-items">
            {order?.products.map((item, index) => (
              <div key={index} className="order-item">
                <div className="item-info">
                  <div className="item-details">
                    <h4>Product ID: {item.productId}</h4>
                    <p>Quantity: {item.quantity}</p>
                  </div>
                </div>
                <div className="item-price">₹{(item.price * item.quantity).toFixed(2)}</div>
              </div>
            ))}
          </div>
          
          <div className="order-summary">
            <div className="summary-row">
              <span>Total:</span>
              <span>₹{order?.products.reduce((sum, item) => sum + (item.price * item.quantity), 0).toFixed(2)}</span>
            </div>
          </div>
        </div>
        
        {paymentStatus === "paid" || paymentStatus === "cod" ? (
          <div className="action-buttons">
            <Link to="/" className="continue-shopping-btn">Continue Shopping</Link>
            <Link to="/account/orders" className="view-orders-btn">View All Orders</Link>
          </div>
        ) : (
          <div className="payment-section">
            {paymentStatus === "failed" && (
              <div className="payment-error">
                <p>Payment failed. Please try again or choose a different payment method.</p>
              </div>
            )}
            
            {paymentMethod === "Cash On Delivery" ? (
              <button 
                className="pay-now-btn"
                onClick={continueWithCOD}
                disabled={processingPayment}
              >
                {processingPayment ? 'Processing...' : 'Place Order with COD'}
              </button>
            ) : (
              <button 
                className={`pay-now-btn ${processingPayment ? 'processing' : ''}`}
                onClick={processPayment}
                disabled={processingPayment}
              >
                {processingPayment ? 'Processing Payment...' : 'Pay Now'}
              </button>
            )}
            
            <p className="secure-payment-note">
              <svg viewBox="0 0 24 24" width="16" height="16">
                <path fill="currentColor" d="M18 8h-1V6c0-2.76-2.24-5-5-5S7 3.24 7 6v2H6c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V10c0-1.1-.9-2-2-2zM9 6c0-1.66 1.34-3 3-3s3 1.34 3 3v2H9V6zm9 14H6V10h12v10zm-6-3c1.1 0 2-.9 2-2s-.9-2-2-2-2 .9-2 2 .9 2 2 2z" />
              </svg>
              Secure Payment Processing
            </p>
          </div>
        )}
      </div>
      
      <Footer />
    </div>
  );
};

export default OrderConfirmation;
