import React, { useState, useEffect } from 'react';
import './Slider.css';

import one from "../../Assets/Images/img1.jpg"
import two from "../../Assets/Images/img2.jpg"
import three from "../../Assets/Images/img3.jpg"

const Slider = () => {
  const images = [image1, image2, image3, image4];

  // State to track the current image index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to go to the next image
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Set up the timer to change images automatically every 3 seconds
  useEffect(() => {
    const interval = setInterval(nextSlide, 3000); // Change image every 3 seconds
    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []);

  return (
    <div className="slider">
      <div
        className="slider-background"
        style={{ backgroundImage: `url(${images[currentIndex]})` }}
      >
        <div className="tagline">
          <h2>Your Tagline Here</h2>
        </div>
      </div>
    </div>
  );
};

export default Slider;
