import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const OrderTracking = () => {
  const { orderId } = useParams();
  const [orderTracking, setOrderTracking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Predefined order status stages with icons and descriptions
  const orderStages = {
    'order received': {
      icon: '📦',
      description: 'Your order has been received and is being processed'
    },
    'reached to nearest hub': {
      icon: '🏢',
      description: 'Order has reached the nearest distribution hub'
    },
    'shipped': {
      icon: '🚚',
      description: 'Your order is on its way'
    },
    'out for delivered': {
      icon: '🚲',
      description: 'Order is out for final delivery'
    },
    'delivered': {
      icon: '✅',
      description: 'Order has been successfully delivered'
    }
  };

  useEffect(() => {
    const fetchOrderTracking = async () => {
        try {
          const token = localStorage.getItem('token');
          
          // If no token exists
          if (!token) {
            console.error('No authentication token found');
            setError('Please log in to view order tracking');
            setLoading(false);
            return;
          }
      
          const response = await axios.get(`http://localhost:5002/orders/tracking/${orderId}`, {
            headers: { 
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });
        } catch (err) {
          // Detailed error logging
          console.error('Full error object:', err);
          console.error('Error response:', err.response);
          console.error('Error message:', err.message);
      
          if (err.response) {
            // The request was made and the server responded with a status code
            // that falls out of the range of 2xx
            switch (err.response.status) {
              case 401:
                setError('Unauthorized. Please log in again.');
                break;
              case 403:
                setError('You do not have permission to view this order.');
                break;
              case 404:
                setError('Order tracking information not found.');
                break;
              default:
                setError(`Server error: ${err.response.status}`);
            }
          } else if (err.request) {
            // The request was made but no response was received
            setError('No response from server. Check your network connection.');
          } else {
            // Something happened in setting up the request
            setError('Error setting up the request. Please try again.');
          }
          
          setLoading(false);
        }
      };

    fetchOrderTracking();
  }, [orderId]);

  if (loading) return <div>Loading tracking information...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="order-tracking-container">
      <h2>Order Tracking</h2>
      <div className="tracking-status">
        <h3>Current Status: {orderTracking.status.toUpperCase()}</h3>
      </div>
      <div className="tracking-timeline">
        {orderTracking.timeline.map((stage, index) => (
          <div key={index} className="tracking-stage">
            <div className="stage-icon">
              {orderStages[stage.status]?.icon || '📍'}
            </div>
            <div className="stage-details">
              <h4>{stage.status.toUpperCase()}</h4>
              <p>{orderStages[stage.status]?.description}</p>
              <small>{new Date(stage.date).toLocaleDateString('en-US', {
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })}</small>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Detailed Order Information Component
const OrderDetails = () => {
  const { orderId } = useParams();
  const [orderDetails, setOrderDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5002/orders/${orderId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOrderDetails(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch order details');
        setLoading(false);
      }
    };

    fetchOrderDetails();
  }, [orderId]);

  if (loading) return <div>Loading order details...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="order-details-container">
      <div className="order-summary">
        <h2>Order Summary</h2>
        <div className="order-info">
          <div className="info-row">
            <span>Order ID:</span>
            <span>{orderId}</span>
          </div>
          <div className="info-row">
            <span>Order Date:</span>
            <span>{new Date(orderDetails.createdAt).toLocaleDateString()}</span>
          </div>
          <div className="info-row">
            <span>Total Amount:</span>
            <span>₹{orderDetails.totalAmount.toFixed(2)}</span>
          </div>
          <div className="info-row">
            <span>Delivery Status:</span>
            <span>{orderDetails.deliveryStatus}</span>
          </div>
        </div>
      </div>

      <div className="order-items">
        <h2>Order Items</h2>
        {orderDetails.products.map((product, index) => (
          <div key={index} className="product-item">
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>Quantity: {product.quantity}</p>
              <p>Price: ₹{product.price.toFixed(2)}</p>
            </div>
          </div>
        ))}
      </div>

      <OrderTracking />
    </div>
  );
};

export { OrderDetails, OrderTracking };
