import React, { useState, useEffect } from 'react';
import { FaUserCircle } from 'react-icons/fa';
import { FiEdit, FiShoppingCart, FiHeart, FiBox, FiCreditCard, FiBell, FiLogOut } from 'react-icons/fi';
import './Account.css';
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";

const Account = () => {
    const [activeTab, setActiveTab] = useState('profile');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [authMode, setAuthMode] = useState('login'); // 'login' or 'signup'
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        address: '',
        password: '',
        confirmPassword: ''
    });
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        phone: '',
        address: ''
    });
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    // Check if user is already logged in on component mount
    useEffect(() => {
        const token = localStorage.getItem('token');
        const storedUserData = localStorage.getItem('userData');
        
        if (token && storedUserData) {
            setIsLoggedIn(true);
            setUserData(JSON.parse(storedUserData));
        }
    }, []);

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const toggleAuthMode = () => {
        setAuthMode(authMode === 'login' ? 'signup' : 'login');
        setError(''); // Clear any previous errors
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError('');

        try {
            // Determine which API endpoint to use based on auth mode
            const endpoint = authMode === 'login' ? 
                'http://localhost:5002/login' : 
                'http://localhost:5002/signup';

            // For a signup request, check if passwords match
            if (authMode === 'signup' && formData.password !== formData.confirmPassword) {
                setError('Passwords do not match');
                setIsLoading(false);
                return;
            }

            // Prepare request data - only send relevant fields
            const requestData = authMode === 'login' ? 
                { email: formData.email, password: formData.password } : 
                { 
                    name: formData.name, 
                    email: formData.email, 
                    phone: formData.phone, 
                    address: formData.address, 
                    password: formData.password 
                };

            const response = await fetch(endpoint, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestData)
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Authentication failed');
            }

            // Store the token for future authenticated requests
            localStorage.setItem('token', data.token);

            // Store user profile data
            const userProfileData = {
                name: data.user.name || formData.name,
                email: data.user.email || formData.email,
                phone: data.user.phone || formData.phone,
                address: data.user.address || formData.address
            };

            setUserData(userProfileData);
            localStorage.setItem('userData', JSON.stringify(userProfileData));
            setIsLoggedIn(true);

        } catch (error) {
            console.error('Authentication error:', error);
            setError(error.message || 'An error occurred during authentication');
        } finally {
            setIsLoading(false);
        }
    };

    const handleLogout = () => {
        // Clear stored data
        localStorage.removeItem('token');
        localStorage.removeItem('userData');
        
        // Reset state
        setIsLoggedIn(false);
        setUserData({
            name: '',
            email: '',
            phone: '',
            address: ''
        });
        setFormData({
            name: '',
            email: '',
            phone: '',
            address: '',
            password: '',
            confirmPassword: ''
        });
    };

    // Auth Form Component
    const AuthForm = () => (
        <div className="auth-container">
            <div className="auth-box">
                <h1>{authMode === 'login' ? 'Login' : 'Sign Up'}</h1>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={handleSubmit}>
                    {authMode === 'signup' && (
                        <>
                            <div className="form-group">
                                <label>Name</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    required
                                    placeholder="Enter your full name"
                                />
                            </div>
                            <div className="form-group">
                                <label>Phone</label>
                                <input
                                    type="tel"
                                    name="phone"
                                    value={formData.phone}
                                    onChange={handleInputChange}
                                    required
                                    placeholder="Enter your phone number"
                                />
                            </div>
                            <div className="form-group">
                                <label>Address</label>
                                <textarea
                                    name="address"
                                    value={formData.address}
                                    onChange={handleInputChange}
                                    required
                                    placeholder="Enter your complete address"
                                    rows="3"
                                ></textarea>
                            </div>
                        </>
                    )}
                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            required
                            placeholder="Enter your email address"
                        />
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            required
                            placeholder="Enter your password"
                        />
                    </div>
                    {authMode === 'signup' && (
                        <div className="form-group">
                            <label>Confirm Password</label>
                            <input
                                type="password"
                                name="confirmPassword"
                                value={formData.confirmPassword}
                                onChange={handleInputChange}
                                required
                                placeholder="Re-enter your password"
                            />
                        </div>
                    )}
                    <button 
                        type="submit" 
                        className="auth-button"
                        disabled={isLoading}
                    >
                        {isLoading ? 'Processing...' : (authMode === 'login' ? 'Login' : 'Sign Up')}
                    </button>
                </form>
                <p className="auth-toggle">
                    {authMode === 'login' ? "Don't have an account? " : "Already have an account? "}
                    <button onClick={toggleAuthMode} className="toggle-btn">
                        {authMode === 'login' ? 'Sign Up' : 'Login'}
                    </button>
                </p>
            </div>
        </div>
    );

    return (
        <div>
            <Header />
            {!isLoggedIn ? (
                <AuthForm />
            ) : (
                <div className="account-container">
                    <div className="sidebar">
                        <div className="profile-info">
                            <FaUserCircle className="profile-icon" />
                            <h2>{userData.name}</h2>
                            <p>{userData.email}</p>
                        </div>
                        <nav className="anav-links">
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'profile' ? 'active' : ''}`}
                                onClick={() => handleTabClick('profile')}
                            >
                                <FiEdit className="icon" />
                                Profile
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'orders' ? 'active' : ''}`}
                                onClick={() => handleTabClick('orders')}
                            >
                                <FiShoppingCart className="icon" />
                                Orders
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'wishlist' ? 'active' : ''}`}
                                onClick={() => handleTabClick('wishlist')}
                            >
                                <FiHeart className="icon" />
                                Wishlist
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'current-orders' ? 'active' : ''}`}
                                onClick={() => handleTabClick('current-orders')}
                            >
                                <FiBox className="icon" />
                                Current Orders
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'payment' ? 'active' : ''}`}
                                onClick={() => handleTabClick('payment')}
                            >
                                <FiCreditCard className="icon" />
                                Payment
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'notifications' ? 'active' : ''}`}
                                onClick={() => handleTabClick('notifications')}
                            >
                                <FiBell className="icon" />
                                Notifications
                            </a>
                            <a href="#" className="anav-link logout" onClick={handleLogout}>
                                <FiLogOut className="icon" />
                                Logout
                            </a>
                        </nav>
                    </div>

                    <div className="content">
                        {activeTab === 'profile' && (
                            <div className="profile-tab">
                                <h1>Profile</h1>
                                <div className="profile-details">
                                    <div className="detail-row">
                                        <label>Name:</label>
                                        <span>{userData.name}</span>
                                    </div>
                                    <div className="detail-row">
                                        <label>Email:</label>
                                        <span>{userData.email}</span>
                                    </div>
                                    <div className="detail-row">
                                        <label>Phone:</label>
                                        <span>{userData.phone}</span>
                                    </div>
                                    <div className="detail-row">
                                        <label>Address:</label>
                                        <span>{userData.address}</span>
                                    </div>
                                    <button className="edit-btn">
                                        <FiEdit className="icon" />
                                        Edit Profile
                                    </button>
                                </div>
                            </div>
                        )}
                        {activeTab === 'orders' && (
                            <div className="orders-tab">
                                <h1>Orders</h1>
                                <p>Your order history will appear here.</p>
                            </div>
                        )}
                        {activeTab === 'wishlist' && (
                            <div className="wishlist-tab">
                                <h1>Wishlist</h1>
                                <p>Items you've saved to your wishlist will appear here.</p>
                            </div>
                        )}
                        {activeTab === 'current-orders' && (
                            <div className="current-orders-tab">
                                <h1>Current Orders</h1>
                                <p>Your active orders will appear here.</p>
                            </div>
                        )}
                        {activeTab === 'payment' && (
                            <div className="payment-tab">
                                <h1>Payment</h1>
                                <p>Your payment methods will appear here.</p>
                            </div>
                        )}
                        {activeTab === 'notifications' && (
                            <div className="notifications-tab">
                                <h1>Notifications</h1>
                                <p>Your notifications will appear here.</p>
                            </div>
                        )}
                    </div>
                </div>
            )}
            <Footer />
        </div>
    );
};

export default Account;
