import "./Offers.css"
import Greps from "../../Assets/Images/greps.jpg"
import Add from "../../Assets/Images/add.png"
import Minus from "../../Assets/Images/minus.png"
import React, { useState } from "react";


    const Offers = () => {
        const [quantity, setQuantity] = useState(1); 
        const pricePerUnit = 200; 

        const totalPrice = quantity * pricePerUnit;

        const handleIncrement = () => {
            setQuantity((prevQuantity) => prevQuantity + 1);
        };

        const handleDecrement = () => {
            setQuantity((prevQuantity) => (prevQuantity > 1 ? prevQuantity - 1 : 1)); // Prevent going below 1
        };

        return (
            <div className="offer-container">
                <div className="offerbox">
                    <img className="offerimg" src={Greps} alt="Fresh, Juicy Grapes" />
                    <h1>Fresh, Juicy Grapes</h1>
                    <h3>Price: {totalPrice}</h3>
                    <div className="price">
                        <img src={Add} alt="Add" className="icon" onClick={handleIncrement} />
                        <span className="price-value">{quantity}</span>
                        <img
                            src={Minus}
                            alt="Minus"
                            className="icon"
                            onClick={handleDecrement}
                        />
                    </div>
                    <button className="offer-btn">Add to cart</button>
                </div>
            </div>
        );
    };


export default Offers
