import React, { useState, useEffect } from 'react';
import './Slider.css'; 
import one from "../../Assets/Images/"

const Slider = () => {
  // Array of images for background
  const images = [
    'https://via.placeholder.com/1200x600/FF5733/FFFFFF?text=Image+1',
    'https://via.placeholder.com/1200x600/33FF57/FFFFFF?text=Image+2',
    'https://via.placeholder.com/1200x600/3357FF/FFFFFF?text=Image+3',
    'https://via.placeholder.com/1200x600/FF33A8/FFFFFF?text=Image+4',
  ];

  // State to track the current image index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to go to the next image
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Set up the timer to change images automatically every 3 seconds
  useEffect(() => {
    const interval = setInterval(nextSlide, 3000); // Change image every 3 seconds
    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []);

  return (
    <div className="slider">
      <div
        className="slider-background"
        style={{ backgroundImage: `url(${images[currentIndex]})` }}
      >
        <div className="tagline">
          <h2>Your Tagline Here</h2>
        </div>
      </div>
    </div>
  );
};

export default Slider;
