import React, { useState, useEffect } from 'react';
import { FaUserCircle, FaEye, FaEyeSlash } from 'react-icons/fa';
import { FiEdit, FiShoppingCart, FiHeart, FiBox, FiCreditCard, FiBell, FiLogOut } from 'react-icons/fi';
import './Account.css';
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import axios from 'axios';

// API base URL
const API_BASE_URL = 'http://localhost:5002';

const Account = () => {
    const [activeTab, setActiveTab] = useState('profile');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [authMode, setAuthMode] = useState('login'); // 'login' or 'signup'
    const [showPassword, setShowPassword] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        rePassword: '',
        phone: '',
        address: ''
    });
    const [userData, setUserData] = useState({
        name: '',
        email: '',
        phone: '',
        address: ''
    });
    const [errors, setErrors] = useState({});
    const [statusMessage, setStatusMessage] = useState(''); // For debugging and user feedback

    // Check if user is logged in on component mount
    useEffect(() => {
        const token = localStorage.getItem('token');
        const storedUserData = localStorage.getItem('userData');
        
        console.log("Token from localStorage:", token);
        console.log("User data from localStorage:", storedUserData);
        
        if (token && storedUserData) {
            try {
                const parsedUserData = JSON.parse(storedUserData);
                setUserData(parsedUserData);
                setIsLoggedIn(true);
                setStatusMessage('Logged in with stored user data');
                
                // After setting initial data from localStorage, fetch the latest user data
                fetchUserProfile(token);
            } catch (error) {
                console.error('Error parsing stored user data:', error);
                localStorage.removeItem('userData');
            }
        } else {
            setStatusMessage('No stored credentials found, please log in');
        }
    }, []);
    
    // Function to fetch user profile data
    const fetchUserProfile = async (token) => {
        try {
            // Attempt to get user profile from API
            const response = await axios.get(`${API_BASE_URL}/profile`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            
            console.log("Profile API response:", response.data);
            
            if (response.data && response.data.user) {
                const updatedUserData = {
                    name: response.data.user.name || userData.name,
                    email: response.data.user.email || userData.email,
                    phone: response.data.user.phone || userData.phone,
                    address: response.data.user.address || userData.address
                };
                
                setUserData(updatedUserData);
                localStorage.setItem('userData', JSON.stringify(updatedUserData));
                setStatusMessage('User profile data updated from API');
            }
        } catch (error) {
            console.error('Failed to fetch user profile:', error);
            setStatusMessage('Could not fetch updated profile from API');
            // Continue with locally stored data, no need to show error to user
        }
    };

    // Reset form data
    const resetForm = () => {
        setFormData({
            name: '',
            email: '',
            password: '',
            rePassword: '',
            phone: '',
            address: ''
        });
        setErrors({});
    };

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const toggleAuthMode = () => {
        setAuthMode(authMode === 'login' ? 'signup' : 'login');
        setErrors({});
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const validateForm = () => {
        const newErrors = {};
        
        if (authMode === 'signup') {
            if (!formData.name.trim()) newErrors.name = 'Name is required';
            if (!formData.phone.trim()) newErrors.phone = 'Phone number is required';
            if (!formData.address.trim()) newErrors.address = 'Address is required';
            if (formData.password !== formData.rePassword) {
                newErrors.rePassword = 'Passwords do not match';
            }
        }
        
        if (!formData.email.trim()) {
            newErrors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Email is invalid';
        }
        
        if (!formData.password) {
            newErrors.password = 'Password is required';
        } else if (formData.password.length < 6) {
            newErrors.password = 'Password must be at least 6 characters';
        }
        
        return newErrors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }
        
        setIsLoading(true);
        setStatusMessage(`Attempting to ${authMode}...`);
        
        try {
            if (authMode === 'signup') {
                // Call signup API with full URL
                const signupData = {
                    name: formData.name,
                    email: formData.email,
                    password: formData.password,
                    rePassword: formData.rePassword,
                    phone: formData.phone,
                    address: formData.address
                };
                
                console.log("Sending signup data:", signupData);
                const response = await axios.post(`${API_BASE_URL}/signup`, signupData);
                console.log("Signup response:", response.data);
                
                if (response.data && response.data.token) {
                    // Store the user data directly after signup
                    const newUserData = {
                        name: formData.name,
                        email: formData.email,
                        phone: formData.phone,
                        address: formData.address
                    };
                    
                    // Store token and user data in localStorage
                    localStorage.setItem('token', response.data.token);
                    localStorage.setItem('userData', JSON.stringify(newUserData));
                    
                    // Update state
                    setUserData(newUserData);
                    setIsLoggedIn(true);
                    setStatusMessage('Signup successful! You are now logged in.');
                    resetForm();
                } else {
                    // If no token in response but otherwise successful
                    resetForm();
                    setAuthMode('login');
                    setStatusMessage('Signup successful! Please login with your credentials.');
                }
            } else {
                // Call login API with full URL
                const loginData = {
                    email: formData.email,
                    password: formData.password
                };
                
                console.log("Sending login data:", loginData);
                const response = await axios.post(`${API_BASE_URL}/login`, loginData);
                console.log("Login response:", response.data);
                
                if (response.data && response.data.token) {
                    const token = response.data.token;
                    
                    // Initialize user info with email from the form
                    let userInfo = {
                        email: formData.email,
                        name: '',
                        phone: '',
                        address: ''
                    };
                    
                    // Check if response contains user data directly
                    if (response.data.user) {
                        userInfo = {
                            ...userInfo,
                            ...response.data.user
                        };
                    } else if (response.data.userData) {
                        userInfo = {
                            ...userInfo,
                            ...response.data.userData
                        };
                    } else if (response.data.data && response.data.data.user) {
                        userInfo = {
                            ...userInfo,
                            ...response.data.data.user
                        };
                    }
                    
                    // Try to decode JWT token to get user info if available
                    try {
                        const tokenParts = token.split('.');
                        if (tokenParts.length === 3) {
                            const payload = JSON.parse(atob(tokenParts[1]));
                            console.log("Decoded token payload:", payload);
                            
                            if (payload.name) userInfo.name = payload.name;
                            if (payload.email) userInfo.email = payload.email;
                            if (payload.phone) userInfo.phone = payload.phone;
                            if (payload.address) userInfo.address = payload.address;
                        }
                    } catch (err) {
                        console.error("Error decoding token:", err);
                    }
                    
                    // Store token and user data in localStorage
                    localStorage.setItem('token', token);
                    localStorage.setItem('userData', JSON.stringify(userInfo));
                    
                    // Update state
                    setUserData(userInfo);
                    setIsLoggedIn(true);
                    setStatusMessage('Login successful!');
                    resetForm();
                    
                    // After successful login, try to fetch complete profile
                    fetchUserProfile(token);
                } else {
                    setStatusMessage('Login response received but no token found');
                    setErrors({ auth: 'Invalid response from server' });
                }
            }
        } catch (error) {
            console.error('Authentication error:', error);
            setStatusMessage(`Authentication error: ${error.message}`);
            
            // Handle API errors
            if (error.response) {
                console.error('Error response details:', error.response.status, error.response.data);
                
                if (error.response.status === 401) {
                    setErrors({ auth: 'Invalid email or password' });
                } else if (error.response.data && error.response.data.message) {
                    setErrors({ auth: error.response.data.message });
                } else if (error.response.data && error.response.data.code === 11000) {
                    // Handle MongoDB duplicate key error
                    if (error.response.data.keyPattern && error.response.data.keyPattern.phone) {
                        setErrors({ phone: 'This phone number is already registered with an account' });
                    } else if (error.response.data.keyPattern && error.response.data.keyPattern.email) {
                        setErrors({ email: 'This email is already registered with an account' });
                    } else {
                        setErrors({ auth: 'A user with this information already exists' });
                    }
                } else {
                    setErrors({ auth: 'Authentication failed. Please try again.' });
                }
            } else {
                setErrors({ auth: 'Network error. Please check your connection.' });
            }
        } finally {
            setIsLoading(false);
        }
    };

    const handleLogout = () => {
        try {
            const token = localStorage.getItem('token');
            
            // Optional: Call logout API to invalidate token on server
            if (token) {
                axios.post(`${API_BASE_URL}/logout`, {}, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }).catch(err => console.log('Logout API error:', err));
            }
        } catch (error) {
            console.error('Logout error:', error);
        } finally {
            // Clear token and reset state
            localStorage.removeItem('token');
            localStorage.removeItem('userData');
            setIsLoggedIn(false);
            setActiveTab('profile');
            resetForm();
            setStatusMessage('You have been logged out');
        }
    };

    // Debug section that can be removed in production
    const debugSection = (
        <div style={{ padding: '10px', background: '#f0f0f0', marginTop: '20px', borderRadius: '5px' }}>
            <h3>Debug Info (remove in production)</h3>
            <p>Status: {statusMessage}</p>
            <p>isLoggedIn: {isLoggedIn ? 'true' : 'false'}</p>
            <p>Token in localStorage: {localStorage.getItem('token') ? 'Yes' : 'No'}</p>
            <p>Current auth mode: {authMode}</p>
            <div>
                <strong>User Data:</strong>
                <pre>{JSON.stringify(userData, null, 2)}</pre>
            </div>
            <button onClick={() => console.log({
                isLoggedIn,
                userData,
                formData,
                errors,
                token: localStorage.getItem('token')
            })}>
                Log State to Console
            </button>
        </div>
    );

    // Function to handle profile edit (placeholder)
    const handleEditProfile = () => {
        // In a real implementation, this would open a form to edit profile details
        alert("Edit profile functionality would be implemented here");
    };

    // Render authentication forms when not logged in
    if (!isLoggedIn) {
        return (
            <div>
                <Header />
                <div className="auth-container">
                    <div className="auth-form-container">
                        <h1>{authMode === 'login' ? 'Login' : 'Sign Up'}</h1>
                        
                        {errors.auth && <div className="auth-error">{errors.auth}</div>}
                        
                        <form onSubmit={handleSubmit} className="auth-form">
                            {authMode === 'signup' && (
                                <div className="form-group">
                                    <label htmlFor="name">Full Name</label>
                                    <input
                                        type="text"
                                        id="name"
                                        name="name"
                                        value={formData.name}
                                        onChange={handleInputChange}
                                        placeholder="Enter your full name"
                                    />
                                    {errors.name && <div className="form-error">{errors.name}</div>}
                                </div>
                            )}
                            
                            <div className="form-group">
                                <label htmlFor="email">Email</label>
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    placeholder="Enter your email"
                                />
                                {errors.email && <div className="form-error">{errors.email}</div>}
                            </div>
                            
                            <div className="form-group">
                                <label htmlFor="password">Password</label>
                                <div className="password-input-container">
                                    <input
                                        type={showPassword ? "text" : "password"}
                                        id="password"
                                        name="password"
                                        value={formData.password}
                                        onChange={handleInputChange}
                                        placeholder="Enter your password"
                                    />
                                    <button 
                                        type="button" 
                                        className="toggle-password" 
                                        onClick={togglePasswordVisibility}
                                    >
                                        {showPassword ? <FaEyeSlash /> : <FaEye />}
                                    </button>
                                </div>
                                {errors.password && <div className="form-error">{errors.password}</div>}
                            </div>
                            
                            {authMode === 'signup' && (
                                <>
                                    <div className="form-group">
                                        <label htmlFor="rePassword">Confirm Password</label>
                                        <input
                                            type="password"
                                            id="rePassword"
                                            name="rePassword"
                                            value={formData.rePassword}
                                            onChange={handleInputChange}
                                            placeholder="Confirm your password"
                                        />
                                        {errors.rePassword && (
                                            <div className="form-error">{errors.rePassword}</div>
                                        )}
                                    </div>
                                    
                                    <div className="form-group">
                                        <label htmlFor="phone">Phone</label>
                                        <input
                                            type="tel"
                                            id="phone"
                                            name="phone"
                                            value={formData.phone}
                                            onChange={handleInputChange}
                                            placeholder="Enter your phone number"
                                        />
                                        {errors.phone && <div className="form-error">{errors.phone}</div>}
                                    </div>
                                    
                                    <div className="form-group">
                                        <label htmlFor="address">Address</label>
                                        <textarea
                                            id="address"
                                            name="address"
                                            value={formData.address}
                                            onChange={handleInputChange}
                                            placeholder="Enter your address"
                                            rows="3"
                                        ></textarea>
                                        {errors.address && <div className="form-error">{errors.address}</div>}
                                    </div>
                                </>
                            )}
                            
                            <button 
                                type="submit" 
                                className="auth-button"
                                disabled={isLoading}
                            >
                                {isLoading 
                                    ? 'Processing...' 
                                    : (authMode === 'login' ? 'Login' : 'Sign Up')}
                            </button>
                        </form>
                        
                        <div className="auth-toggle">
                            {authMode === 'login' ? (
                                <p>Don't have an account? <button onClick={toggleAuthMode}>Sign Up</button></p>
                            ) : (
                                <p>Already have an account? <button onClick={toggleAuthMode}>Login</button></p>
                            )}
                        </div>
                        
                        {/* Debug info - remove in production */}
                        {debugSection}
                    </div>
                </div>
                <Footer />
            </div>
        );
    }

    // Original account page when logged in
    return (
        <div>
            <Header/>
            <div className="account-container">
                <div className="sidebar">
                    <div className="profile-info">
                        <FaUserCircle className="profile-icon" />
                        <h2>{userData.name || 'User'}</h2>
                        <p>{userData.email || 'email@example.com'}</p>
                    </div>
                    <nav className="anav-links">
                        <a
                            href="#"
                            className={`anav-link ${activeTab === 'profile' ? 'active' : ''}`}
                            onClick={() => handleTabClick('profile')}
                        >
                            <FiEdit className="icon" />
                            Profile
                        </a>
                        <a
                            href="#"
                            className={`anav-link ${activeTab === 'orders' ? 'active' : ''}`}
                            onClick={() => handleTabClick('orders')}
                        >
                            <FiShoppingCart className="icon" />
                            Orders
                        </a>
                        <a
                            href="#"
                            className={`anav-link ${activeTab === 'wishlist' ? 'active' : ''}`}
                            onClick={() => handleTabClick('wishlist')}
                        >
                            <FiHeart className="icon" />
                            Wishlist
                        </a>
                        <a
                            href="#"
                            className={`anav-link ${activeTab === 'current-orders' ? 'active' : ''}`}
                            onClick={() => handleTabClick('current-orders')}
                        >
                            <FiBox className="icon" />
                            Current Orders
                        </a>
                        <a
                            href="#"
                            className={`anav-link ${activeTab === 'payment' ? 'active' : ''}`}
                            onClick={() => handleTabClick('payment')}
                        >
                            <FiCreditCard className="icon" />
                            Payment
                        </a>
                        <a
                            href="#"
                            className={`anav-link ${activeTab === 'notifications' ? 'active' : ''}`}
                            onClick={() => handleTabClick('notifications')}
                        >
                            <FiBell className="icon" />
                            Notifications
                        </a>
                        <a href="#" className="anav-link logout" onClick={handleLogout}>
                            <FiLogOut className="icon" />
                            Logout
                        </a>
                    </nav>
                </div>

                <div className="content">
                    {activeTab === 'profile' && (
                        <div className="profile-tab">
                            <h1>Profile</h1>
                            <div className="profile-details">
                                <div className="detail-row">
                                    <label>Name:</label>
                                    <span>{userData.name || 'Not provided'}</span>
                                </div>
                                <div className="detail-row">
                                    <label>Email:</label>
                                    <span>{userData.email || 'Not provided'}</span>
                                </div>
                                <div className="detail-row">
                                    <label>Phone:</label>
                                    <span>{userData.phone || 'Not provided'}</span>
                                </div>
                                <div className="detail-row">
                                    <label>Address:</label>
                                    <span>{userData.address || 'Not provided'}</span>
                                </div>
                                <button className="edit-btn" onClick={handleEditProfile}>
                                    <FiEdit className="icon" />
                                    Edit Profile
                                </button>
                            </div>
                        </div>
                    )}
                    {activeTab === 'orders' && (
                        <div className="orders-tab">
                            <h1>Orders</h1>
                            <p>You don't have any orders yet.</p>
                        </div>
                    )}
                    {activeTab === 'wishlist' && (
                        <div className="wishlist-tab">
                            <h1>Wishlist</h1>
                            <p>Your wishlist is empty.</p>
                        </div>
                    )}
                    {activeTab === 'current-orders' && (
                        <div className="current-orders-tab">
                            <h1>Current Orders</h1>
                            <p>You don't have any active orders at the moment.</p>
                        </div>
                    )}
                    {activeTab === 'payment' && (
                        <div className="payment-tab">
                            <h1>Payment</h1>
                            <p>No payment methods added yet.</p>
                        </div>
                    )}
                    {activeTab === 'notifications' && (
                        <div className="notifications-tab">
                            <h1>Notifications</h1>
                            <p>You don't have any notifications.</p>
                        </div>
                    )}
                    
                    {/* Debug info - remove in production */}
                    {debugSection}
                </div>
            </div>
            <Footer/>
        </div>
    );
};

export default Account;
