import React, { useState } from 'react';
import { 
  User, 
  ShoppingCart, 
  Heart, 
  Package, 
  CreditCard, 
  Bell, 
  LogOut 
} from 'lucide-react';
import './Account.css';
import "../../components/Header/Header.css"

const Account = () => {
  const [activeSection, setActiveSection] = useState('profile');
  const [user, setUser] = useState({
    name: 'John Doe',
    email: 'john.doe@example.com',
    phone: '+91 9876543210',
    address: 'Plot 123, Krushivrund Farm Area, Nashik'
  });

  const renderSection = () => {
    switch(activeSection) {
      case 'profile':
        return <ProfileSection user={user} setUser={setUser} />;
      case 'orders':
        return <OrdersSection />;
      case 'wishlist':
        return <WishlistSection />;
      case 'current-orders':
        return <CurrentOrdersSection />;
      case 'payments':
        return <PaymentMethodsSection />;
      case 'notifications':
        return <NotificationsSection />;
      default:
        return <ProfileSection user={user} setUser={setUser} />;
    }
  };

  return (
    <div className="user-account-container">
      <div className="user-sidebar">
        <div className="user-profile-header">
          <img 
            src="/api/placeholder/120/120" 
            alt="Profile" 
            className="profile-image" 
          />
          <h2>{user.name}</h2>
          <p>{user.email}</p>
        </div>
        <nav className="sidebar-nav">
          {[
            { icon: <User />, label: 'Profile', section: 'profile' },
            { icon: <ShoppingCart />, label: 'Order History', section: 'orders' },
            { icon: <Heart />, label: 'Wishlist', section: 'wishlist' },
            { icon: <Package />, label: 'Current Orders', section: 'current-orders' },
            { icon: <CreditCard />, label: 'Payment Methods', section: 'payments' },
            { icon: <Bell />, label: 'Notifications', section: 'notifications' },
            { icon: <LogOut />, label: 'Logout', section: 'logout' }
          ].map(item => (
            <button 
              key={item.section}
              className={`nav-item ${activeSection === item.section ? 'active' : ''}`}
              onClick={() => setActiveSection(item.section)}
            >
              {item.icon}
              <span>{item.label}</span>
            </button>
          ))}
        </nav>
      </div>
      <div className="user-content">
        {renderSection()}
      </div>
    </div>
  );
};

const ProfileSection = ({ user, setUser }) => (
  <div className="section-container">
    <h1>My Profile</h1>
    <div className="profile-details">
      <div className="detail-group">
        <label>Name</label>
        <input 
          type="text" 
          value={user.name} 
          onChange={(e) => setUser({...user, name: e.target.value})} 
        />
      </div>
      <div className="detail-group">
        <label>Email</label>
        <input 
          type="email" 
          value={user.email} 
          onChange={(e) => setUser({...user, email: e.target.value})} 
        />
      </div>
      <div className="detail-group">
        <label>Phone</label>
        <input 
          type="tel" 
          value={user.phone} 
          onChange={(e) => setUser({...user, phone: e.target.value})} 
        />
      </div>
      <div className="detail-group">
        <label>Address</label>
        <textarea 
          value={user.address} 
          onChange={(e) => setUser({...user, address: e.target.value})}
        />
      </div>
      <button className="update-btn">Update Profile</button>
    </div>
  </div>
);

const OrdersSection = () => (
  <div className="section-container">
    <h1>Order History</h1>
    <div className="orders-list">
      <div className="order-item">
        <div className="order-details">
          <span>Order #12345</span>
          <span>Raisin Box (500g)</span>
          <span>₹450</span>
          <span className="status delivered">Delivered</span>
        </div>
        <button>Reorder</button>
      </div>
    </div>
  </div>
);

const WishlistSection = () => (
  <div className="section-container">
    <h1>My Wishlist</h1>
    <div className="wishlist-items">
      {/* Wishlist items would be dynamically populated */}
    </div>
  </div>
);

const CurrentOrdersSection = () => (
  <div className="section-container">
    <h1>Current Orders</h1>
    {/* Order tracking details */}
  </div>
);

const PaymentMethodsSection = () => (
  <div className="section-container">
    <h1>Payment Methods</h1>
    {/* Payment method management */}
  </div>
);

const NotificationsSection = () => (
  <div className="section-container">
    <h1>Notifications</h1>
    {/* Notification preferences */}
  </div>
);

export default Account;
