import { useState, useEffect } from "react";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import "./Cart.css"; // Add your styling

const Cart = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (id) => {
    const updatedCart = cart.filter((item) => item._id !== id);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  return (
    <div>
      <Header />
      <div className="cart-hero"> {/* ✅ Same hero section */}
        <h1>Your Cart</h1>
        <p>Review and manage your selected items</p>
      </div>

      <div className="cart-container"> {/* ✅ Same container class */}
        {cart.length > 0 ? (
          <div className="cart-grid"> {/* ✅ Same grid layout */}
            {cart.map((product) => (
              <div key={product._id} className="cart-card"> {/* ✅ Same card styling */}
                <img
                  src={`http://localhost:5002${product.images}`}
                  alt={product.name}
                  className="cart-image"
                />
                <h2 className="cart-name">{product.name}</h2>
                <p className="cart-description">{product.shortDescription}</p>
                <p className="product-price">Price: ₹{product.price}</p>
                <p className="product-quantity">Quantity: {product.quantity}</p>
                <button className="add-to-cart-btn" onClick={() => removeFromCart(product._id)}>
                  Remove
                </button>
                <button className="buynow">Buy Now</button>
              </div>
            ))}
          </div>
        ) : (
          <p className="no-products">Your cart is empty.</p>
        )}
      </div>

      <Footer />
    </div>
  );
};
export default Cart;
