import React, { useState, useEffect } from "react";
import axios from "axios";
import toast, { Toaster } from "react-hot-toast";
import Input from "./../../components/Input";
import { getCurrentUser } from "./../../utils/common";
import "./Account.css";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";

function Account() {
  const [formType, setFormType] = useState("signup");
  const [userLoggedIn, setUserLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState("");

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    address: "",
    password: "",
    rePassword: "",
  });

  useEffect(() => {
    // Check if user is already logged in
    const currentUser = getCurrentUser();
    if (currentUser) {
      setUserLoggedIn(true);
      setUserData(currentUser);
    }
  }, []);

  const handleInputChange = (field, value) => {
    setFormData({ ...formData, [field]: value });
    setError("");
  };

  const processLogin = async () => {
    toast.loading("Please wait...", { 
      style: {
        background: "#333",
        color: "#fff"
      }
    });
    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/login`,
        {
          email: formData.email,
          password: formData.password,
        }
      );
      localStorage.setItem("e-commerce-user-token", response.data.token);
      localStorage.setItem(
        "e-commerce-user-details",
        JSON.stringify(response.data.data)
      );
      toast.dismiss();
      toast.success(response.data.message, {
        style: {
          background: "#4caf50",
          color: "#fff"
        }
      });
      setUserData(response.data.data);
      setUserLoggedIn(true);
    } catch (err) {
      toast.dismiss();
      setError(err?.response?.data?.message);
      toast.error(err?.response?.data?.message, {
        style: {
          background: "#f44336",
          color: "#fff"
        }
      });
    }
  };

  const processSignup = async () => {
    toast.loading("Please wait...", {
      style: {
        background: "#333",
        color: "#fff"
      }
    });
    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/signup`,
        formData
      );
      toast.dismiss();
      toast.success(response.data.message, {
        style: {
          background: "#4caf50",
          color: "#fff"
        }
      });
      setFormData({
        name: "",
        email: "",
        phone: "",
        address: "",
        password: "",
        rePassword: "",
      });
      setFormType("login");
    } catch (err) {
      toast.dismiss();
      setError(err?.response?.data?.message);
      toast.error(err?.response?.data?.message, {
        style: {
          background: "#f44336",
          color: "#fff"
        }
      });
    }
  };

  const handleSubmit = () => {
    if (formType === "login") {
      processLogin();
    } else {
      processSignup();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("e-commerce-user-token");
    localStorage.removeItem("e-commerce-user-details");
    setUserLoggedIn(false);
    setUserData(null);
    toast.success("Logged out successfully", {
      style: {
        background: "#4caf50",
        color: "#fff"
      }
    });
  };

  return (
    <div className="account-page">
      <Header />
      <Toaster 
        position="top-center"
        toastOptions={{
          duration: 3000,
        }}
      />

      <div className="account-container">
        {!userLoggedIn ? (
          <div className="account-form-container">
            <div className="form-header">
              <h2>{formType === "login" ? "Login to Your Account" : "Create New Account"}</h2>
              <div className="form-toggle-links">
                <span 
                  className={formType === "login" ? "active" : ""} 
                  onClick={() => setFormType("login")}
                >
                  Login
                </span>
                <span 
                  className={formType === "signup" ? "active" : ""} 
                  onClick={() => setFormType("signup")}
                >
                  Signup
                </span>
              </div>
            </div>

            <div className="form-content">
              <div className="form-fields">
                {formType === "signup" && (
                  <Input
                    type="text"
                    placeholder="Full Name"
                    value={formData.name}
                    onChange={(val) => handleInputChange("name", val)}
                  />
                )}

                <Input
                  type="email"
                  placeholder="Email Address"
                  value={formData.email}
                  onChange={(val) => handleInputChange("email", val)}
                />

                {formType === "signup" && (
                  <>
                    <Input
                      type="tel"
                      placeholder="Phone Number"
                      value={formData.phone}
                      onChange={(val) => handleInputChange("phone", val)}
                    />
                    <Input
                      type="text"
                      placeholder="Address"
                      value={formData.address}
                      onChange={(val) => handleInputChange("address", val)}
                    />
                  </>
                )}

                <Input
                  type="password"
                  placeholder="Password"
                  value={formData.password}
                  onChange={(val) => handleInputChange("password", val)}
                />

                {formType === "signup" && (
                  <Input
                    type="password"
                    placeholder="Confirm Password"
                    value={formData.rePassword}
                    onChange={(val) => handleInputChange("rePassword", val)}
                  />
                )}
              </div>

              {error && <div className="error-message">{error}</div>}

              <div className="form-actions">
                <button 
                  className="submit-button" 
                  onClick={handleSubmit}
                >
                  {formType === "login" ? "Login" : "Signup"}
                </button>
                <button 
                  className="cancel-button" 
                  onClick={() => window.location.href = "/"}
                >
                  Cancel
                </button>
              </div>

              <div className="form-toggle-text">
                {formType === "login" ? (
                  <p>
                    Don't have an account?{" "}
                    <span onClick={() => setFormType("signup")}>Signup</span>
                  </p>
                ) : (
                  <p>
                    Already have an account?{" "}
                    <span onClick={() => setFormType("login")}>Login</span>
                  </p>
                )}
              </div>
            </div>
          </div>
        ) : (
          <div className="user-profile-container">
            <div className="profile-header">
              <div className="profile-avatar">
                {userData.name ? userData.name.charAt(0).toUpperCase() : "U"}
              </div>
              <h2>Welcome, {userData.name}!</h2>
            </div>

            <div className="profile-details">
              <div className="detail-card">
                <h3>Personal Information</h3>
                <div className="detail-row">
                  <span className="detail-label">Name:</span>
                  <span className="detail-value">{userData.name}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Email:</span>
                  <span className="detail-value">{userData.email}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Phone:</span>
                  <span className="detail-value">{userData.phone || "Not provided"}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Address:</span>
                  <span className="detail-value">{userData.address || "Not provided"}</span>
                </div>
              </div>

              <div className="account-actions">
                <button 
                  className="shop-button" 
                  onClick={() => window.location.href = "/"}
                >
                  Continue Shopping
                </button>
                <button 
                  className="logout-button" 
                  onClick={handleLogout}
                >
                  Logout
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      <Footer />
    </div>
  );
}

export default Account;
