import React, { useState } from 'react'
import { FaUser } from "react-icons/fa";
import { FaHeart } from "react-icons/fa";
import { FaShoppingBag } from "react-icons/fa";
import companylogo from '../../Assets/Images/foodmartlogo.png'
import '../Header/Header.css'
import { Box, Button, Typography, Modal, Grid } from "@mui/material";
import environment from '../../environment';
import Swal from "sweetalert2";


const Header = () => {

    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const [productName, setProductName] = useState()
    const [productDesc, setProductDesc] = useState();
    const [productCategory, setProductCategory] = useState('')
    const [productPrice, setProductPrice] = useState('')
    const [discount, setDiscount] = useState('')
    const [productNameError, setProductNameError] = useState()
    const [productDescError, setProductDescError] = useState();
    const [productCategoryError, setProductCategoryError] = useState('')
    const [productPriceError, setProductPriceError] = useState('')
    const [discountError, setDiscountError] = useState('')
    const [productImage,setProductImage]=useState('')
    const [productImageError,setProductImageError]=useState('')

    const handleClose = () => {
        handleClearForm();
        setOpen(false);
    };


    const handleClearForm = () => {
        setProductName('')
        setProductDesc('')
        setProductCategory('')
        setProductPrice('')
        setDiscount('')
        setProductNameError('')
        setProductDescError('')
        setProductCategoryError('')
        setProductPriceError('')
        setDiscountError('')


    }

    const handleChangeProductName=(event)=>{
        const value = event.target.value 
        if(value.length===0){
            setProductNameError('Please provide a valid information')
        }else{
            setProductNameError('')
        }
        setProductName(value)
    }
    const handleChangeProductDesc=(event)=>{
        const value = event.target.value 
        if(value.length===0){
            setProductDescError('Please provide a valid information')
        }else{
            setProductDescError('')
        }
        setProductDesc(value)
    }
    const handleChangeProductCategory=(event)=>{
        const value = event.target.value 
        if(value.length===0){
            setProductCategoryError('Please provide a valid information')
        }else{
            setProductCategoryError('')
        }
        setProductCategory(value)
    }
    const handleChangeProductPrice=(event)=>{
        const value = event.target.value 
        if(value.length===0){
            setProductPriceError('Please provide a valid information')
        }else{
            setProductPriceError('')
        }
        setProductPrice(value)
    }
    const handleChangeDiscount=(event)=>{
        const value = event.target.value 
        if(value.length===0){
            setDiscountError('Please provide a valid information')
        }else{
            setDiscountError('')
        }
        setDiscount(value)
    }
    const handleChangeProductImage = (event) => {
        const value = event.target.value
        if (value.length === 0) {
            setProductImageError('Please prevoid a valid information')
        }
        let type = event.target.files[0].type;
        // JPEG, GIF, PNG, TIFF, BMP
        if (type.includes('PDF') || type.includes('pdf') || type.includes("docx") || type.includes("DOCX") || type.includes("document") || type.includes("DOCUM") ||
            type.includes("TXT") || type.includes("txt") || type.includes("MP3") || type.includes("mp3") || type.includes("MP4")
            || type.includes("mp4") || type.includes("AVI") || type.includes("avis")
        ) {
            alert(event.target.files[0].type + " file is not allowed")
        } else {
            setProductImage(event.target.files[0]);
            setProductImageError('')
        }
    }



    const handleAddProduct=async(event)=>{
        // setIsActive(false);
    event.preventDefault();
    let isValid = true
    if (productName.trim() === '') {
      setProductNameError('Please provide a valid information')
      isValid = false;
    }
    if (productDesc.trim() === '') {
      setProductDescError('Please provide a valid information')
      isValid = false;
    }
    if (productCategory.trim() === '') {
      setProductCategoryError('Please provide a valid information')
      isValid = false;
    }
    if (productPrice.trim() === '') {
      setProductPriceError('Please provide a valid information')
      isValid = false;
    }
    if (discount.trim() === '') {
        setDiscountError('Please provide a valid information')
        isValid = false;
      }
 
    // If validation fails, do not proceed
    if (!isValid) {
      return;
    }
 
    // Clear error messages if validation passes
    setProductNameError('')
    setProductDescError('')
    setProductCategoryError('')
    setProductPriceError('')
    setDiscountError('')
 
    const formData = new FormData();
    formData.append('productName',productName);
    formData.append('productDesc',productDesc);
    formData.append('productCategory',productCategory);
    formData.append('productPrice',productPrice);
    formData.append('discount',discount);
    formData.append('image',productImage);

    console.log('formData',formData)
 
    try {
        const response = await fetch(`${environment.config.apiBaseUrl}products/addProduct`, {
            method: 'POST',
            body: formData, // Pass formData as the body
        });

        const result = await response.json();
        handleClearForm();
        setOpen(false);
        console.log("result", result)
        if (result && result.status === 200) {
            Swal.fire({
                title: "GOOD JOB!",
                text: "User added successfully!",
                icon: "success"
            });
        } else {
            Swal.fire({
                title: "Bad JOB!",
                text: `${result.message}`,
                icon: "error"
            });
        }
    } catch (error) {
        handleClearForm();

    }
    // getAllUser(pageNo, pageSize);

}

    
    const style = {
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        width: "50%",
        maxWidth: "1000px",
        height: "auto",
        maxHeight: "90vh",
        bgcolor: "#EBEDF2 !important",
        border: "1px solid #fff",
        borderRadius: "10px",
        boxShadow: 30,
        p: 4,
        overflowY: "auto",
    };
    return (
        <>

            <div className="container-fluid headermainrow ">
                <div className="row header d-flex flex-row mt-2">
                    <div className="col-3 logodiv d-flex justify-content-start align-items-center">
                        <a href="index.html">
                            <img src={companylogo} alt="logo" className="img-fluid" />
                        </a>
                    </div>
                    <div className="col-6">
                        <div className="search-bar row bg-light p-2 my-2 rounded-4">
                            <div className="col-4 d-none d-md-block">
                                <select className="form-select border-0 bg-transparent">
                                    <option>All Categories</option>
                                    <option>Groceries</option>
                                    <option>Drinks</option>
                                    <option>Chocolates</option>
                                </select>
                            </div>
                            <div className="col-11 col-md-7">
                                <form id="search-form" className="text-center" action="index.html" method="post">
                                    <input type="text" className="form-control border-0 bg-transparent" placeholder=" " />
                                </form>
                            </div>
                            <div className="col-1">
                                <svg xmlns="http://www.w3.org/2000/svg" width={24} height={24} viewBox="0 0 24 24"><path fill="currentColor" d="M21.71 20.29L18 16.61A9 9 0 1 0 16.61 18l3.68 3.68a1 1 0 0 0 1.42 0a1 1 0 0 0 0-1.39ZM11 18a7 7 0 1 1 7-7a7 7 0 0 1-7 7Z" /></svg>
                            </div>
                        </div>
                        <div>

                        </div>
                    </div>
                    <div className="col-3 iconsdiv d-flex justify-content-end align-items-center">
                        {/* <div className="support-box text-end d-none d-xl-block">
                            <span className="fs-6 text-muted">For Support?</span>
                            <h5 className="mb-0">+980-34984089</h5>
                        </div> */}
                        <ul className="d-flex justify-content-end list-unstyled m-0">
                            <li className='p-4'>
                                {/* <a href="#" className="rounded-circle bg-light p-4 mx-1"> */}
                                {/* <i class="fa-regular fa-user"></i> */}
                                <FaUser />
                                {/* </a> */}
                            </li>
                            <li className='p-4'>
                                {/* <a href="#" className="rounded-circle bg-light p-4 mx-1"> */}
                                <FaHeart />
                                {/* </a> */}
                            </li>
                            <li className='p-4'>
                                {/* <a href="#" className="rounded-circle bg-light p-4 mx-1" data-bs-toggle="offcanvas" data-bs-target="#offcanvasCart" aria-controls="offcanvasCart"> */}
                                <FaShoppingBag />
                                {/* </a> */}
                            </li>

                        </ul>
                        {/* <div className="cart text-end d-none d-lg-block dropdown">
                            <button className="border-0 bg-transparent d-flex flex-column gap-2 lh-1" type="button" data-bs-toggle="offcanvas" data-bs-target="#offcanvasCart" aria-controls="offcanvasCart">
