                                <Button
                                  variant="contained"
                                  color="primary"
                                  sx={{ marginRight: 2 }}
                                //   onClick={handleUpdateProduct}
                                >
                                  Update
                                </Button>
                              )} */}

                                                        <Button
                                                            variant="solid"
                                                            color="secondary"
                                                            className="btn btn-black btn-border"
                                                            onClick={handleClose}
                                                        >
                                                            Close
                                                        </Button>
                                                    </Grid>
                                                </Box>
                                            </Modal>
                                        </li>
                                    </ul>
                                </div>
                            </div>
