import React, { useState } from 'react';
import { FaUserCircle } from 'react-icons/fa';
import { FiEdit, FiShoppingCart, FiHeart, FiBox, FiCreditCard, FiBell, FiLogOut } from 'react-icons/fi';
import './Account.css';
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";

const Account = () => {
    const [activeTab, setActiveTab] = useState('profile');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [authMode, setAuthMode] = useState('login'); // 'login' or 'signup'
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        address: '',
        password: '',
        confirmPassword: ''
    });

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const toggleAuthMode = () => {
        setAuthMode(authMode === 'login' ? 'signup' : 'login');
        // Clear form data when switching modes
        setFormData({
            name: '',
            email: '',
            phone: '',
            address: '',
            password: '',
            confirmPassword: ''
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Clear stored data
        localStorage.removeItem('token');
        localStorage.removeItem('userData');

        // Reset state
        setIsLoggedIn(false);
        setUserData({
            name: '',
            email: '',
            phone: '',
            address: ''
        });
        setFormData({
            name: '',
            email: '',
            phone: '',
            address: '',
            password: '',
            confirmPassword: ''
        });
        setIsLoggedIn(true);
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setFormData({
            name: '',
            email: '',
            phone: '',
            address: '',
            password: '',
            confirmPassword: ''
        });
    };

    // Auth Form Component
    const AuthForm = () => (
        <div className="auth-container">
            <div className="auth-box">
                <h1>{authMode === 'login' ? 'Login' : 'Sign Up'}</h1>
                <form onSubmit={handleSubmit}>
                    {authMode === 'signup' && (
                        <>
                            <div className="form-group">
                                <label>Name</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Phone</label>
                                <input
                                    type="tel"
                                    name="phone"
                                    value={formData.phone}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Address</label>
                                <textarea
                                    name="address"
                                    value={formData.address}
                                    onChange={handleInputChange}
                                    required
                                    rows="3"
                                />
                            </div>
                        </>
                    )}
                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    {authMode === 'signup' && (
                        <div className="form-group">
                            <label>Confirm Password</label>
                            <input
                                type="password"
                                name="confirmPassword"
                                value={formData.confirmPassword}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                    )}
                    <button type="submit" className="auth-button">
                        {authMode === 'login' ? 'Login' : 'Sign Up'}
                    </button>
                </form>
                <p className="auth-toggle">
                    {authMode === 'login' ? "Don't have an account? " : "Already have an account? "}
                    <button type="button" onClick={toggleAuthMode} className="toggle-btn">
                        {authMode === 'login' ? 'Sign Up' : 'Login'}
                    </button>
                </p>
            </div>
        </div>
    );

    return (
        <div>
            <Header />
            {!isLoggedIn ? (
                <AuthForm />
            ) : (
                <div className="account-container">
                    <div className="sidebar">
                        <div className="profile-info">
                            <FaUserCircle className="profile-icon" />
                            <h2>Sejal Pawar</h2>
                            <p>sejalpawar463@gmail.com</p>
                        </div>
                        <nav className="anav-links">
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'profile' ? 'active' : ''}`}
                                onClick={() => handleTabClick('profile')}
                            >
                                <FiEdit className="icon" />
                                Profile
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'orders' ? 'active' : ''}`}
                                onClick={() => handleTabClick('orders')}
                            >
                                <FiShoppingCart className="icon" />
                                Orders
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'wishlist' ? 'active' : ''}`}
                                onClick={() => handleTabClick('wishlist')}
                            >
                                <FiHeart className="icon" />
                                Wishlist
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'current-orders' ? 'active' : ''}`}
                                onClick={() => handleTabClick('current-orders')}
                            >
                                <FiBox className="icon" />
                                Current Orders
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'payment' ? 'active' : ''}`}
                                onClick={() => handleTabClick('payment')}
                            >
                                <FiCreditCard className="icon" />
                                Payment
                            </a>
                            <a
                                href="#"
                                className={`anav-link ${activeTab === 'notifications' ? 'active' : ''}`}
                                onClick={() => handleTabClick('notifications')}
                            >
                                <FiBell className="icon" />
                                Notifications
                            </a>
                            <a href="#" className="anav-link logout" onClick={handleLogout}>
                                <FiLogOut className="icon" />
                                Logout
                            </a>
                        </nav>
                    </div>

                    <div className="content">
                        {activeTab === 'profile' && (
                            <div className="profile-tab">
                                <h1>Profile</h1>
                                <div className="profile-details">
                                    <div className="detail-row">
                                        <label>Name:</label>
                                        <span>Sejal Pawar</span>
                                    </div>
                                    <div className="detail-row">
                                        <label>Email:</label>
                                        <span>sejalpawar463@gmail.com</span>
                                    </div>
                                    <div className="detail-row">
                                        <label>Phone:</label>
                                        <span>+91 1234567890</span>
                                    </div>
                                    <div className="detail-row">
                                        <label>Address:</label>
                                        <span>123 Main Street, Nashik</span>
                                    </div>
                                    <button className="edit-btn">
                                        <FiEdit className="icon" />
                                        Edit Profile
                                    </button>
                                </div>
                            </div>
                        )}
                        {activeTab === 'orders' && (
                            <div className="orders-tab">
                                <h1>Orders</h1>
                            </div>
                        )}
                        {activeTab === 'wishlist' && (
                            <div className="wishlist-tab">
                                <h1>Wishlist</h1>
                            </div>
                        )}
                        {activeTab === 'current-orders' && (
                            <div className="current-orders-tab">
                                <h1>Current Orders</h1>
                            </div>
                        )}
                        {activeTab === 'payment' && (
                            <div className="payment-tab">
                                <h1>Payment</h1>
                            </div>
                        )}
                        {activeTab === 'notifications' && (
                            <div className="notifications-tab">
                                <h1>Notifications</h1>
                            </div>
                        )}
                    </div>
                </div>
            )}
            <Footer />
        </div>
    );
};

export default Account;
