import React, { useState } from 'react';
import { FaUserCircle } from 'react-icons/fa';
import { FiEdit, FiShoppingCart, FiHeart, FiBox, FiCreditCard, FiBell, FiLogOut } from 'react-icons/fi';
import './Account.css';
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";

const Account = () => {
    const [activeTab, setActiveTab] = useState('profile');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [authMode, setAuthMode] = useState('login'); // 'login' or 'signup'
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        address: '',
        password: '',
        confirmPassword: ''
    });

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const toggleAuthMode = () => {
        setAuthMode(authMode === 'login' ? 'signup' : 'login');
        // Clear form data when switching modes
        setFormData({
            name: '',
            email: '',
            phone: '',
            address: '',
            password: '',
            confirmPassword: ''
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // In a real app, you would handle authentication here
        // For now, we'll just simulate a successful login/signup
        setIsLoggedIn(true);
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setFormData({
            name: '',
            email: '',
            phone: '',
            address: '',
            password: '',
            confirmPassword: ''
        });
    };

    // Auth Form Component
    const AuthForm = () => (
        <div className="auth-container">
            <div className="auth-box">
                <h1>{authMode === 'login' ? 'Login' : 'Sign Up'}</h1>
                <form onSubmit={handleSubmit}>
                    {authMode === 'signup' && (
                        <>
                            <div className="form-group">
                                <label>Name</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Phone</label>
                                <input
                                    type="tel"
                                    name="phone"
                                    value={formData.phone}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Address</label>
                                <textarea
                                    name="address"
                                    value={formData.address}
                                    onChange={handleInputChange}
                                    required
                                    rows="3"
                                />
                            </div>
                        </>
                    )}
                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    {authMode === 'signup' && (
                        <div className="form-group">
                            <label>Confirm Password</label>
                            <input
                                type="password"
                                name="confirmPassword"
                                value={formData.confirmPassword}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                    )}
                    <button type="submit" className="auth-button">
                        {authMode === 'login' ? 'Login' : 'Sign Up'}
                    </button>
                </form>
                <p className="auth-toggle">
                    {authMode === 'login' ? "Don't have an account? " : "Already have an account? "}
                    <button type="button" onClick={toggleAuthMode} className="toggle-btn">
                        {authMode === 'login' ? 'Sign Up' : 'Login'}
                    </button>
                </p>
            </div>
        </div>
    );

    return (
        <div>
            <Header />
            {!isLoggedIn ? (
                <AuthForm />
            ) : (
                <div className="account-container">
                    <div className="sidebar">
