import React, { useState, useEffect } from "react";
import axios from "axios";
import toast, { Toaster } from "react-hot-toast";
import Button from "./../../components/Button";
import Input from "./../../components/Input";
import { getCurrentUser } from "./../../utils/common";
import "./Account.css"; // Import the CSS file
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer"

function Account() {
    const [activeForm, setActiveForm] = useState("login");

    const [loginData, setLoginData] = useState({
        email: "",
        password: "",
    });

    const [signupData, setSignupData] = useState({
        name: "",
        email: "",
        phone: "",
        address: "",
        password: "",
        rePassword: "",
    });

    const [error, setError] = useState("");

    const processLogin = async () => {
        toast.loading("Please wait...");
        try {
            const response = await axios.post(
                `${process.env.REACT_APP_API_URL}/login`,
                loginData
            );
            localStorage.setItem("e-commerce-user-token", response.data.token);
            localStorage.setItem(
                "e-commerce-user-details",
                JSON.stringify(response.data.data)
            );
            toast.dismiss();
            toast.success(response.data.message);
            setLoginData({
                email: "",
                password: "",
            });
            setTimeout(() => {
                window.location.href = "/dashboard";
            }, 3000);
        } catch (err) {
            toast.dismiss();
            setError(err?.response?.data?.message);
            toast.error(err?.response?.data?.message);
        }
    };

    const processSignup = async () => {
        toast.loading("Please wait...");
        try {
            const response = await axios.post(
                `${process.env.REACT_APP_API_URL}/signup`,
                signupData
            );
            toast.dismiss();
            toast.success(response.data.message);
            setSignupData({
                name: "",
                email: "",
                phone: "",
                address: "",
                password: "",
                rePassword: "",
            });
            setTimeout(() => {
                setActiveForm("login");
            }, 3000);
        } catch (err) {
            toast.dismiss();
            setError(err?.response?.data?.message);
            toast.error(err?.response?.data?.message);
        }
    };

    useEffect(() => {
        // Check if user is already logged in
        const currentUser = getCurrentUser();
        if (currentUser) {
            toast.success("You are already logged in. Redirecting to dashboard...");
            setTimeout(() => {
                window.location.href = "/dashboard";
            }, 3000);
        }
    }, []);

    return (
        <div>
            <div className="auth-container">
                <div className="auth-tabs">
                    <button
                        className={`auth-tab ${activeForm === 'login' ? 'active-tab' : ''}`}
                        onClick={() => {
                            setActiveForm("login");
                            setError("");
                        }}
                    >
                        Login
                    </button>
                    <button
                        className={`auth-tab ${activeForm === 'signup' ? 'active-tab' : ''}`}
                        onClick={() => {
                            setActiveForm("signup");
                            setError("");
                        }}
                    >
                        Signup
                    </button>
                </div>

                <h1 className="auth-heading">
                    {activeForm === "login" ? "Login" : "Signup"}
                </h1>

                <div className="auth-form-container">
                    {activeForm === "login" ? (
                        <>
                            <Input
                                label={"Email"}
                                val={loginData.email}
                                onChange={(val) => {
                                    setLoginData({ ...loginData, email: val });
                                    setError("");
                                }}
                            />
                            <Input
                                label={"Password"}
                                type="password"
                                val={loginData.password}
                                onChange={(val) => {
                                    setLoginData({ ...loginData, password: val });
                                    setError("");
                                }}
                            />
                            <p className="error-message">{error}</p>
                            <p className="auth-switch-text">
                                Don't have an account?{" "}
                                <button
                                    className="auth-switch-button"
                                    onClick={() => {
                                        setActiveForm("signup");
                                        setError("");
                                    }}
                                >
                                    Signup
                                </button>
                            </p>
                            <div className="auth-buttons">
                                <Button
                                    label="Cancel"
                                    onClick={() => {
                                        window.location.href = "/";
                                    }}
                                    variant={"danger"}
                                />
                                <Button
                                    label="Login"
                                    onClick={() => processLogin()}
                                    variant={"primary"}
                                />
                            </div>
                        </>
                    ) : (
                        <>
                            <Input
                                label={"Name"}
                                val={signupData.name}
                                onChange={(val) => {
                                    setSignupData({ ...signupData, name: val });
                                    setError("");
                                }}
                            />
                            <Input
                                label={"Email"}
                                val={signupData.email}
                                onChange={(val) => {
                                    setSignupData({ ...signupData, email: val });
                                    setError("");
                                }}
                            />
                            <Input
                                label={"Phone"}
                                val={signupData.phone}
                                onChange={(val) => {
                                    setSignupData({ ...signupData, phone: val });
                                    setError("");
                                }}
                            />
                            <Input
                                label={"Address"}
                                val={signupData.address}
                                onChange={(val) => {
                                    setSignupData({ ...signupData, address: val });
                                    setError("");
                                }}
                            />
                            <Input
                                label={"Password"}
                                type="password"
                                val={signupData.password}
                                onChange={(val) => {
                                    setSignupData({ ...signupData, password: val });
                                    setError("");
                                }}
                            />
                            <Input
                                label={"Re-enter Password"}
                                val={signupData.rePassword}
                                type="password"
                                onChange={(val) => {
                                    setSignupData({ ...signupData, rePassword: val });
                                    setError("");
                                }}
                            />
                            <p className="error-message">{error}</p>
                            <p className="auth-switch-text">
                                Already have an account?{" "}
                                <button
                                    className="auth-switch-button"
                                    onClick={() => {
                                        setActiveForm("login");
                                        setError("");
                                    }}
                                >
                                    Login
                                </button>
                            </p>
                            <div className="auth-buttons">
                                <Button
                                    label="Cancel"
                                    onClick={() => {
                                        window.location.href = "/";
                                    }}
                                    variant={"danger"}
                                />
                                <Button
                                    label="Signup"
                                    onClick={() => processSignup()}
                                    variant={"primary"}
                                />
                            </div>
                        </>
                    )}
                </div>
                <Toaster />
            </div>
        </div>
    );
}

export default Account;
