import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const OrderTracking = () => {
  const { orderId } = useParams();
  const [orderTracking, setOrderTracking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Predefined order status stages with icons and descriptions
  const orderStages = {
    'order received': {
      icon: 'üì¶',
      description: 'Your order has been received and is being processed'
    },
    'reached to nearest hub': {
      icon: 'üè¢',
      description: 'Order has reached the nearest distribution hub'
    },
    'shipped': {
      icon: 'üöö',
      description: 'Your order is on its way'
    },
    'out for delivered': {
      icon: 'üö≤',
      description: 'Order is out for final delivery'
    },
    'delivered': {
      icon: '‚úÖ',
      description: 'Order has been successfully delivered'
    }
  };

  useEffect(() => {
    const fetchOrderTracking = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5002/orders/tracking/${orderId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        // Remove duplicate entries
        const uniqueTimeline = response.data.timeline.filter((item, index, self) =>
          index === self.findIndex((t) => (
            t.status === item.status && t.date === item.date
          ))
        );

        // Sort timeline by date in descending order
        const sortedTimeline = uniqueTimeline.sort((a, b) => new Date(b.date) - new Date(a.date));

        setOrderTracking({
          ...response.data,
          timeline: sortedTimeline
        });
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch order tracking information');
        setLoading(false);
      }
    };

    fetchOrderTracking();
  }, [orderId]);

  if (loading) return <div>Loading tracking information...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="order-tracking-container">
      <h2>Order Tracking</h2>
      <div className="tracking-status">
        <h3>Current Status: {orderTracking.status.toUpperCase()}</h3>
      </div>
      <div className="tracking-timeline">
        {orderTracking.timeline.map((stage, index) => (
          <div key={index} className="tracking-stage">
            <div className="stage-icon">
              {orderStages[stage.status]?.icon || 'üìç'}
            </div>
            <div className="stage-details">
              <h4>{stage.status.toUpperCase()}</h4>
              <p>{orderStages[stage.status]?.description}</p>
              <small>{new Date(stage.date).toLocaleDateString('en-US', {
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })}</small>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Detailed Order Information Component
const OrderDetails = () => {
  const { orderId } = useParams();
  const [orderDetails, setOrderDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5002/orders/${orderId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOrderDetails(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch order details');
        setLoading(false);
      }
    };

    fetchOrderDetails();
  }, [orderId]);

  if (loading) return <div>Loading order details...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="order-details-container">
      <div className="order-summary">
        <h2>Order Summary</h2>
        <div className="order-info">
          <div className="info-row">
            <span>Order ID:</span>
            <span>{orderId}</span>
          </div>
          <div className="info-row">
            <span>Order Date:</span>
            <span>{new Date(orderDetails.createdAt).toLocaleDateString()}</span>
          </div>
          <div className="info-row">
            <span>Total Amount:</span>
            <span>‚Çπ{orderDetails.totalAmount.toFixed(2)}</span>
          </div>
          <div className="info-row">
            <span>Delivery Status:</span>
            <span>{orderDetails.deliveryStatus}</span>
          </div>
        </div>
      </div>

      <div className="order-items">
        <h2>Order Items</h2>
        {orderDetails.products.map((product, index) => (
          <div key={index} className="product-item">
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>Quantity: {product.quantity}</p>
              <p>Price: ‚Çπ{product.price.toFixed(2)}</p>
            </div>
          </div>
        ))}
      </div>

      <OrderTracking />
    </div>
  );
};

export { OrderDetails, OrderTracking };
